    private void loadTable() {
    DefaultTableModel model = (DefaultTableModel) EtudiantTable.getModel();
    model.setRowCount(0);

    for (Etudiant e : es.findAll()) {
        model.addRow(new Object[]{
            e.getId(),
            e.getNom(),
            e.getPrenom(),
            e.getFiliere(),
            e.getNiveau()
        });
    }
}

    public EtudiantForme() {
        initComponents();
        loadTable();
        EtudiantTable.addMouseListener(new java.awt.event.MouseAdapter() {
        @Override
        public void mouseClicked(java.awt.event.MouseEvent evt) {
        int row = EtudiantTable.getSelectedRow();
        selectedId = Integer.parseInt(EtudiantTable.getValueAt(row, 0).toString());

        NomTxt.setText(EtudiantTable.getValueAt(row, 1).toString());
        PrenomTxt.setText(EtudiantTable.getValueAt(row, 2).toString());
        FiliereTxt.setText(EtudiantTable.getValueAt(row, 3).toString());
        NiveauTxt.setText(EtudiantTable.getValueAt(row, 4).toString());
    }
});
    }

