    private void AjouterbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_AjouterbtnActionPerformed
    String nom = NomTxt.getText();
    String prenom = PrenomTxt.getText();
    String filiere = FiliereTxt.getText();
    String niveau = NiveauTxt.getText();
    
    Etudiant e = new Etudiant(0, nom, prenom, filiere, niveau);

    if (es.insert(e)) {
        JOptionPane.showMessageDialog(this, "Étudiant ajouté !");
        loadTable();
    } else {
        JOptionPane.showMessageDialog(this, "Erreur d'ajout !");
    }        
    }//GEN-LAST:event_AjouterbtnActionPerformed

