    private void ModifierbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ModifierbtnActionPerformed
        if (selectedId == -1) {
        JOptionPane.showMessageDialog(this, "Sélectionnez un étudiant !");
        return;
    }

    Etudiant e = new Etudiant(
            selectedId,
            NomTxt.getText(),
            PrenomTxt.getText(),
            FiliereTxt.getText(),
            NiveauTxt.getText()
    );

    if (es.update(e)) {
        JOptionPane.showMessageDialog(this, "Étudiant modifié !");
        loadTable();
    } else {
        JOptionPane.showMessageDialog(this, "Erreur modification !");
    }
    }//GEN-LAST:event_ModifierbtnActionPerformed

    private void SupprimerbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SupprimerbtnActionPerformed
        if (selectedId == -1) {
        JOptionPane.showMessageDialog(this, "Sélectionnez un étudiant !");
        return;
    }

    Etudiant e = new Etudiant(selectedId, "", "", "", "");

    if (es.delete(e)) {
        JOptionPane.showMessageDialog(this, "Étudiant supprimé !");
        loadTable();
        selectedId = -1;
    } else {
        JOptionPane.showMessageDialog(this, "Erreur suppression !");
    }
    }//GEN-LAST:event_SupprimerbtnActionPerformed

