    private boolean containsItem(JComboBox comboBox, String item) {
    for(int i = 0; i < comboBox.getItemCount(); i++) {
        if(comboBox.getItemAt(i).equals(item)) return true;
    }
    return false;
    }
    public FiltrageForme() {
        initComponents();
       
      FilieretList.addItem("Toutes");
      for(Etudiant e : etudiantService.findAll()) {
       if(FilieretList.getItemCount() == 1 || !containsItem(FilieretList, e.getFiliere())) {
        FilieretList.addItem(e.getFiliere());
    }
    }


      ModuleList.addItem("Tous");
     for(Module m : moduleService.findAll()) {
     ModuleList.addItem(m.getNom());
    }
    }

