    private void FiltrerbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_FiltrerbtnActionPerformed

        String filiere = FilieretList.getSelectedItem().toString();
        String module = ModuleList.getSelectedItem().toString();
        String session = SessionTxt.getText().trim();

        // Vider la table
        ((javax.swing.table.DefaultTableModel)NotesTable.getModel()).setRowCount(0);

        // Récupérer toutes les notes
        List<Note> notes = noteService.findAll();

        for(Note n : notes) {
            boolean match = true;

            // Filtrage par filière
            if(!filiere.equals("Toutes") && !n.getEtudiant().getFiliere().equals(filiere)) {
                match = false;
            }

            // Filtrage par module
            if(!module.equals("Tous") && !n.getModule().getNom().equals(module)) {
                match = false;
            }

            // Filtrage par session
            if(!session.isEmpty() && !n.getSession().equalsIgnoreCase(session)) {
                match = false;
            }

            if(match) {
                ((javax.swing.table.DefaultTableModel)NotesTable.getModel()).addRow(
                    new Object[]{
                        n.getId(),
                        n.getEtudiant().getNom() + " " + n.getEtudiant().getPrenom(),
                        n.getModule().getNom(),
                        n.getSession(),
                        n.getValeur()
                    }
                );
            }
        }

    }//GEN-LAST:event_FiltrerbtnActionPerformed


