    private void EnvoyerMotDePassebtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_EnvoyerMotDePassebtnActionPerformed
                                                         
    String email = EmailTxt.getText().trim();
    if(email.isEmpty()) {
        JOptionPane.showMessageDialog(this, "Veuillez entrer votre adresse email");
        return;
    }

    // Chercher l'utilisateur par email
    User user = null;
    for(User u : us.findAll()) {
        if(u.getEmail().equalsIgnoreCase(email)) {
            user = u;
            break;
        }
    }

    if(user == null) {
        JOptionPane.showMessageDialog(this, "Email non trouvé !");
        return;
    }

    // Générer mot de passe temporaire
    String tempPwd = generateRandomPassword(8);

    // Mettre à jour le mot de passe dans la base
    user.setPassword(tempPwd);
    if(us.updatePassword(user)) {

        // Préparer le contenu de l'email
        String subject = "Réinitialisation de votre mot de passe";
        String body = "Bonjour " + user.getUsername() + ",\n\n"
                + "Voici votre mot de passe temporaire : " + tempPwd + "\n"
                + "Merci de le changer après la connexion.";

        // Envoyer l'email dans un thread séparé
        final String userEmail = user.getEmail();
        
        // Afficher un message de chargement
        JOptionPane.showMessageDialog(this, 
            "Envoi de l'email en cours...\nVeuillez patienter.", 
            "Information", 
            JOptionPane.INFORMATION_MESSAGE);
        
        new Thread(() -> {
            try {
                EmailUtil.sendEmail(userEmail, subject, body);
                
                // Retour sur le thread EDT pour l'UI
                javax.swing.SwingUtilities.invokeLater(() -> {
                    JOptionPane.showMessageDialog(this, 
                        "Un email avec votre mot de passe temporaire a été envoyé !");
                    
                    Connexion login = new Connexion(); 
                    login.setVisible(true);
                    this.dispose();
                });
                
            } catch (Exception e) {
                javax.swing.SwingUtilities.invokeLater(() -> {
                    JOptionPane.showMessageDialog(this, 
                        "Erreur lors de l'envoi de l'email : " + e.getMessage());
                });
            }
        }).start();
    }

    }//GEN-LAST:event_EnvoyerMotDePassebtnActionPerformed

