    private void loadTable() {
    DefaultTableModel model = (DefaultTableModel) ModuleTable.getModel();
    model.setRowCount(0);
    for (Module m : ms.findAll()) {
        model.addRow(new Object[]{
            m.getId(),
            m.getNom(),
            m.getCoefficient()
            
        });
    }
}
    private void initTableClick() {
        ModuleTable.addMouseListener(new java.awt.event.MouseAdapter() {
            @Override
            public void mouseClicked(java.awt.event.MouseEvent evt) {
                int row = ModuleTable.getSelectedRow();
                if(row != -1){
                selectedId = Integer.parseInt(ModuleTable.getValueAt(row, 0).toString());
                System.out.println("SelectedId"+ selectedId);
                NomTxt.setText(ModuleTable.getValueAt(row, 1).toString());
                CoefficientTxt.setText(ModuleTable.getValueAt(row, 2).toString());
                }
            }
        });
    }
      
    public ModuleForme() {
        initComponents();
        loadTable();
        initTableClick();
    }

