    private void AfficherbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_AfficherbtnActionPerformed
              

    String nom = NomTxt.getText().trim();
    String prenom = PrenomTxt.getText().trim();
    String session = SessionTxt.getText().trim().toLowerCase();

    if(nom.isEmpty() || prenom.isEmpty() || session.isEmpty()){
        JOptionPane.showMessageDialog(this, "Veuillez remplir tous les champs !");
        return;
    }

    if(!session.equals("printemps") && !session.equals("automne")){
        JOptionPane.showMessageDialog(this, 
            "La session doit être : printemps ou automne !");
        return;
    }

    // Récupérer étudiant
    Etudiant et = etudiantService.findByNomPrenom(nom, prenom);

    if(et == null){
        JOptionPane.showMessageDialog(this, "Étudiant introuvable !");
        return;
    }

    DefaultTableModel model = (DefaultTableModel) ModuleTable.getModel();
    model.setRowCount(0);

    // Récupération notes depuis DB
    List<Note> notes = noteService.findAll(); // ou ResultatSession(session)

    double total = 0;
    int count = 0;

    for(Note n : notes){
    // on supprime les espaces et on compare insensible à la casse
    if(n.getEtudiant().getId() == et.getId() 
       && n.getSession().trim().equalsIgnoreCase(session))
    {
        model.addRow(new Object[]{
            n.getModule().getNom(),
            n.getSession(),
            String.format("%.2f", n.getValeur())  // arrondi à 2 décimales
        });

        total += n.getValeur();
        count++;
    }

    }

    if(count > 0){
        double moyenne = total / count;
        MoyenneCalcule.setText("Moyenne : " + String.format("%.2f", moyenne));
    } else {
        MoyenneCalcule.setText("Moyenne : Aucune Note");
    
}

    }//GEN-LAST:event_AfficherbtnActionPerformed


