    /**
     * Creates new form Notes
     */
    private void loadEtudiants(){
        EtudiantList.removeAllItems();
        for(Etudiant e : es.findAll()){
            EtudiantList.addItem(e);
            
        }
    }
    private void loadModules(){
        ModuleList.removeAllItems();
        for(Module e : ms.findAll()){
            ModuleList.addItem(e);
            
        }
    }
    
    
    private void loadNotesTable() {
    String[] cols = {"ID", "Étudiant", "Module", "Session", "Note"};
    model = new DefaultTableModel(cols, 0);
    NoteTable.setModel(model);

    for (Note n : ns.findAll()) {
        model.addRow(new Object[]{
            n.getId(),
            n.getEtudiant().getNom(),
            n.getModule().getNom(),
            n.getSession(),
            n.getValeur()
        });
    }
   }
   
   private void tableNotesMouseClicked(java.awt.event.MouseEvent evt) {
    int row = NoteTable.getSelectedRow();
    if (row == -1) return;

    
    NoteTxt.setText(model.getValueAt(row, 3).toString());

    // Repositionner les combos :
    String etNom = model.getValueAt(row, 0).toString();
    String moNom = model.getValueAt(row, 1).toString();

    // sélectionner l'étudiant
    for (int i = 0; i < EtudiantList.getItemCount(); i++) {
        Object item = EtudiantList.getItemAt(i);
        if (item instanceof Etudiant) {
            Etudiant e = (Etudiant) item;
            if (e.getNom() != null && e.getNom().equals(etNom)) {
                EtudiantList.setSelectedIndex(i);
                break;
            }
        }
    }

    // sélectionner le module
    for (int i = 0; i < ModuleList.getItemCount(); i++) {
        Object item = ModuleList.getItemAt(i);
        if (item instanceof Module) {
            Module m = (Module) item;
            if (m.getNom() != null && m.getNom().equals(moNom)) {
                ModuleList.setSelectedIndex(i);
                break;
            }
        }
    }

    String session = SessionTxt.getText();
}
 
    public NoteForme() {
        initComponents();
        loadEtudiants();
        loadModules();
        loadNotesTable();
        
    }

