    private void AjouterbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_AjouterbtnActionPerformed
     Etudiant e = (Etudiant) EtudiantList.getSelectedItem();
     Module m = (Module) ModuleList.getSelectedItem();

    if (e == null) {
    JOptionPane.showMessageDialog(this, "Sélectionnez un étudiant !");
    return;
   }

   if (m == null) {
    JOptionPane.showMessageDialog(this, "Sélectionnez un module !");
    return;
   }

    String session = SessionTxt.getText().trim();
    if (session.isEmpty()) {
    JOptionPane.showMessageDialog(this, "Veuillez saisir la session !");
    return;
    }

   if (!session.equalsIgnoreCase("Printemps") &&
    !session.equalsIgnoreCase("Automne")) {
    JOptionPane.showMessageDialog(this, "La session doit être 'Printemps' ou 'Automne'.");
    return;
   }

    String valeurTxt = NoteTxt.getText().trim();
    double valeur;

   if (valeurTxt.isEmpty()) {
    JOptionPane.showMessageDialog(this, "Veuillez saisir la note !");
    return;
   }

   try {
    valeur = Double.parseDouble(valeurTxt);
   } catch (NumberFormatException ex) {
    JOptionPane.showMessageDialog(this, "La note doit être un nombre !");
    return;
   }

   if (valeur < 0 || valeur > 20) {
    JOptionPane.showMessageDialog(this, "La note doit être entre 0 et 20 !");
    return;
   }

   Note n = new Note(0, e, m, session, valeur);

  if (ns.insert(n)) {
    JOptionPane.showMessageDialog(this, "Note ajoutée avec succès !");
    loadNotesTable();
   } else {
    JOptionPane.showMessageDialog(this, "Erreur lors de l'ajout !");
   }
    }//GEN-LAST:event_AjouterbtnActionPerformed

    private void ModifierbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ModifierbtnActionPerformed
                                             
    int row = NoteTable.getSelectedRow();
    if (row == -1) {
        JOptionPane.showMessageDialog(this, "Sélectionnez une note !");
        return;
    }

    // Récupérer l'ID de la note dans la table
    int id = Integer.parseInt(model.getValueAt(row, 0).toString());

    // Récupérer les valeurs des champs
    Etudiant e = (Etudiant) EtudiantList.getSelectedItem();
    Module m = (Module) ModuleList.getSelectedItem();

    if (e == null) {
        JOptionPane.showMessageDialog(this, "Sélectionnez un étudiant !");
        return;
    }

    if (m == null) {
        JOptionPane.showMessageDialog(this, "Sélectionnez un module !");
        return;
    }

    String session = SessionTxt.getText().trim();
    if (session.isEmpty()) {
        JOptionPane.showMessageDialog(this, "Veuillez saisir la session !");
        return;
    }

    if (!session.equalsIgnoreCase("Printemps") &&
        !session.equalsIgnoreCase("Automne")) {

        JOptionPane.showMessageDialog(this,
                "La session doit être 'Printemps' ou 'Automne'.");
        return;
    }

    String valeurTxt = NoteTxt.getText().trim();
    double valeur;

    if (valeurTxt.isEmpty()) {
        JOptionPane.showMessageDialog(this, "Veuillez saisir la note !");
        return;
    }

    try {
        valeur = Double.parseDouble(valeurTxt);
    } catch (NumberFormatException ex) {
        JOptionPane.showMessageDialog(this, "La note doit être un nombre !");
        return;
    }

    if (valeur < 0 || valeur > 20) {
        JOptionPane.showMessageDialog(this, "La note doit être entre 0 et 20 !");
        return;
    }

    
    Note n = new Note(id, e, m, session, valeur);

   
    if (ns.update(n)) {
        JOptionPane.showMessageDialog(this, "Note modifiée avec succès !");
        loadNotesTable();
    } else {
        JOptionPane.showMessageDialog(this, "Erreur lors de la modification !");
    }


    }//GEN-LAST:event_ModifierbtnActionPerformed

    private void SupprimerbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SupprimerbtnActionPerformed
      int selectedRow = NoteTable.getSelectedRow();

if (selectedRow == -1) {
    JOptionPane.showMessageDialog(this, "Sélectionnez une note !");
    return;
}

int id = Integer.parseInt(NoteTable.getValueAt(selectedRow, 0).toString());
Note note = ns.findById(id);

if (note == null) {
    JOptionPane.showMessageDialog(this, "Note introuvable !");
    return;
}

int confirm = JOptionPane.showConfirmDialog(
    this,
    "Voulez-vous vraiment supprimer cette note ?",
    "Confirmation",
    JOptionPane.YES_NO_OPTION
);

if (confirm != JOptionPane.YES_OPTION) {
    return;
}

if (ns.delete(note)) {
    JOptionPane.showMessageDialog(this, "Note supprimée avec succès !");
    loadNotesTable();
} else {
    JOptionPane.showMessageDialog(this, "Erreur lors de la suppression !");
}

    }//GEN-LAST:event_SupprimerbtnActionPerformed

