    private void SinscrireBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SinscrireBtnActionPerformed

        String username = NomTxt.getText().trim();
        String email = EmailTxt1.getText().trim();
        String password = new String(PasswordTxt.getPassword()).trim();

        if(username.isEmpty() || email.isEmpty() || password.isEmpty()){
            JOptionPane.showMessageDialog(this, "Veuillez remplir tous les champs !");
            return;
        }

        UserServices us = new UserServices();

        if(us.usernameExists(username)){
            JOptionPane.showMessageDialog(this, "Ce nom d'utilisateur existe déjà !");
            return;
        }

        // Création du User sans role
        User u = new User(username, password, email);

        if(us.signup(u)){
            JOptionPane.showMessageDialog(this, "Compte créé avec succès !");
            NomTxt.setText("");
            EmailTxt1.setText("");
            PasswordTxt.setText("");
        } else {
            JOptionPane.showMessageDialog(this, "Erreur lors de la création du compte !");
        }
    }//GEN-LAST:event_SinscrireBtnActionPerformed

