    private void loadGraph() {
    NoteService service = new NoteService();
    List<Object[]> data = service.getMoyenneParModule();

    DefaultCategoryDataset dataset = new DefaultCategoryDataset();

    for (Object[] row : data) {
        String module = (String) row[0];
        Double moyenne = (Double) row[1];
        dataset.addValue(moyenne, "Moyenne", module);
    }

    JFreeChart chart = ChartFactory.createBarChart(
            "Moyenne des notes par module",
            "Module",
            "Moyenne",
            dataset
    );

    ChartPanel chartPanel = new ChartPanel(chart);

    // On fixe une taille manuelle si jPanelGraph n'a pas encore de taille
    chartPanel.setPreferredSize(new java.awt.Dimension(800, 478));

    jPanelGraph.removeAll(); // Supprime tout contenu précédent
    jPanelGraph.setLayout(new BorderLayout());
    jPanelGraph.add(chartPanel, BorderLayout.CENTER);

    jPanelGraph.revalidate();
    jPanelGraph.repaint();
}


    
    public StatistiqueForme() {
        initComponents();
        loadGraph();
    }

